<style lang="scss">
	@import 'components';
	

  .label {
		overflow-y:hidden;
		margin: 4%;

	}
	
.navbar {
  
  background:$backgroundMain;
  box-shadow: 0 3px 8px 3px $backgroundSecond;
  backdrop-filter: blur(1.5px);
  -webkit-backdrop-filter: blur(1.5px);
  border: 1px solid rgba(255, 255, 255, 0.18);

  position: fixed;
  right: 0;
  transition: width 600ms ease;
  overflow: hidden;
  z-index: 5;
}

.navbar-nav {
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  height: 100%;
}

.nav-item {
  width: 100%;

  
}

.nav-link {
  display: flex;
  align-items: center;
  height: 4rem;
  padding: 3%;
  color: $textColorMain;
  font-weight: 400;
  text-decoration: none;
  filter: grayscale(70%) opacity(0.7);
  transition: 0.3s;

  &:hover {
  filter: grayscale(0%) opacity(1);
  background: $backgroundSecond;
  color: $textColorMain;
}

.nav-extra {
        position: absolute;
        top: 66px;
        right: 0;
        width: 180px;
        display: none;
        z-index: 88888;
    }

.nav-extra li a {
        padding: 10px;
        background: #fff;
        display:none;
    }



svg {
  min-width: 2rem;
  margin: 0 1.25rem;
}
}


.link-text {
  display: none;
  margin-left: 1rem;
  font-size: 2vw;
  padding: 1%;
}


.fa-primary {
  color: $backgroundThird;
}

.fa-secondary {
  color: $extra;
}

.fa-primary,
.fa-secondary {
  transition: $transitSpeed;
}

.logo {
  font-weight: bold;
  text-transform: uppercase;
  margin-bottom: 1rem;
  text-align: center;
  color: $textColorMain;
  background: $backgroundSecond;
  font-size: 1rem;
  letter-spacing: 0.3ch;
  width: 100%;

  svg {
  transform: rotate(180deg);
  transition: $transitSpeed;
}
}


.logo-text
{
  font-style: italic;
  display: inline;
  position: absolute;
  padding: 0 23% 0 0;
  left: -999px;
  transition: $transitSpeed;
}

.navbar:hover .logo svg {
  transform: rotate(0deg);
}

/* Small screens */
@media only screen and (max-width: 600px) {
  .navbar {
    bottom: 0;
    width: 100vw;
    height: 4rem;
    background: $backgroundThird;
  }

  .logo {
    display: none;
  }

  .navbar-nav {
    flex-direction: row;
  }

  .nav-link {
    justify-content: center;
    color: $textColorMain;

    &:hover{
      background-color: $backgroundSecond;
    }

    svg {
  max-width: 1.25rem;
  margin: 0 .25rem;
}
  }

  .nav-extra li a {
        padding: 2px;
        background: #fff;
        display:none;
    

}

  .fa-primary {
  color: $extra;
}

.fa-secondary {
  color: $textColorMain;
}

  main {
    margin: 0;
  }
}

/* Large screens */
@media only screen and (min-width: 600px) {
  .navbar {
    top: 0;
    width: 5rem;
    height: 100vh;
  }

  .nav-item{
    &:last-child {
  margin-top: auto;
}
  }

  .navbar:hover {
    width: 16rem;
  }

  .navbar:hover .link-text {
    display: inline;
  }

  .navbar:hover .logo svg
  {
    margin-left: 11rem;
  }

  .navbar:hover .logo-text
  {
    left: 0px;
  }

  .link-text {
            font-size: 1.5vw;
        }
}

	
	
		
</style>